definition.script:
node {
   cleanWs()
   // Test environment
   def test_environment_repository = "${TEST_ENVIRONMENT_REPOSITORY}"
   def test_environment_branch = "${TEST_ENVIRONMENT_BRANCH}"
   def test_environment_subfolder = "/home/jenkins/tests/"
   // Tests
   def test_repository = "${CUSTOM_TEST_REPOSITORY}"
   def test_branch = "${CUSTOM_TEST_BRANCH}"
   def test_repository_subfolder = "/home/jenkins/tests/python-tests"
   def test_subfolder = "/common/xrd-ui-tests-python"
   def test_order_file = "/common/xrd-ui-tests-python/jenkins/xroad_est_tests_profile.txt"

   def current_workspace = "$WORKSPACE"

   def geckodriver_path = "/home/jenkins/.local/lib/python2.7/site-packages/qautomate/webdrivers/geckodriver_linux64"
   def ignore_errors = "false"

   def python_tests_subfolder = test_repository_subfolder+test_subfolder

   def temp_ws = ""
   def xroad_python_tests = python_tests_subfolder
   def python_path = temp_ws + "/" + xroad_python_tests
   def test_order_profile = test_repository_subfolder + test_order_file
   if ("${FIRST_RUN}" == "True") {
       stage("Checkout and init") {
           sh """
            find ${test_repository_subfolder} -name '*-junit.xml' -exec rm -r {} \\; || true
            rm -f ${test_repository_subfolder}/temp/*.png
            rm -f ${test_repository_subfolder}/temp/*.txt
           """
           checkout([$class: 'GitSCM', branches: [[name: "${test_environment_branch}"]], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'RelativeTargetDirectory', relativeTargetDir: "${test_environment_subfolder}"]], gitTool: 'Default', submoduleCfg: [], userRemoteConfigs: [[url: "${test_environment_repository}"]]])
           checkout([$class: 'GitSCM', branches: [[name: "${test_branch}"]], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'RelativeTargetDirectory', relativeTargetDir: "${test_repository_subfolder}"]], gitTool: 'Default', submoduleCfg: [], userRemoteConfigs: [[url: "${test_repository}"]]])
       }
   }
   def testProfile = readFile(test_order_profile).readLines()
   if ("${FIRST_RUN}" == "True") {
       println("[T] Test list file: ${test_order_profile}")
       println(testProfile.join("\n"))
       println("[T] Total lines: " + testProfile.size())
   }
   def execTestClass = ""
   def execTestFolder = ""
   def execCount = []
   //def temp_ws = pwd()

   def run_tests_param = "${RUN_TESTS}".tokenize(',')
   def run_tests = []
   for (int i = 0; i < run_tests_param.size(); i++) {
       def run_test = run_tests_param[i].trim()
       if (run_test != "" && run_test != "ALL") {
           run_tests.add(run_test)
       }
   }

   if ("${RUN_TESTS}" != "SKIP") {
       def counter = 0
       for (int i = 0; i < testProfile.size(); i++) {
           def line = testProfile[i].trim()
           if (line == "" || line[0] == "#" || line[0] == "[") {
               // Skip these lines
               continue
           }
           // We're here, so we're running the test
           counter++
           tokens = line.tokenize('=')
           def delay = 1
           def testName = tokens[0]
           if (tokens.size() == 2) {
               delay = tokens[1]
           }
           def testTokens = testName.tokenize('.')
           execTestFolder = testTokens[0]
           execTestClass = testTokens[1]

           // If run_tests is empty, then we need to run all of them
           if (run_tests.size() > 0) {
               // Otherwise we'll only run the tests that were supplied inparams
               if (!run_tests.contains(testName) && !run_tests.contains(execTestClass)) {
                   continue
               }
           }

           stage(execTestClass) {
               println("Test "+counter +": " + execTestFolder + "." + execTestClass + ", delay after: " + delay )

               sh """
                   export DISPLAY=:0
                   export PYTHONPATH=$temp_ws/$xroad_python_tests
                   export PYTHONUNBUFFERED=1
                   export PATH=$PATH:$geckodriver_path
                   cd ${python_path}/tests/${execTestFolder}
                   nose2 --plugin nose2.plugins.junitxml --junit-xml ${execTestClass} || ${ignore_errors}
                   mv nose2-junit.xml ${python_tests_subfolder}/temp/${execTestClass}-junit.xml
                """

                sleep(delay.toInteger())
           }
       }
   }
   if ("${JUNIT_RESULTS}" == "True") {
       stage("junit results") {
           sh """
            mv ${python_tests_subfolder}/temp/*-junit.xml ${current_workspace}/ || true
            touch ${current_workspace}/*-junit.xml || true
           """
           archiveArtifacts artifacts: "${python_tests_subfolder}/temp/*.png, ${python_tests_subfolder}/temp/*.txt, ${python_tests_subfolder}/main/config_lxd.ini", allowEmptyArchive: true
           junit "*-junit.xml"
       }
   }
}
